con:config.script:
import com.xifin.clientws.dao.*;
import com.xifin.accnws.dao.*;
import com.xifin.mars.dao.*;
    import com.xifin.qa.config.*;
    import com.xifin.mars.dao.*;

// Get Project - level property Value
// get dbEnv
def dbEnv = testRunner.testCase.testSuite.project.getPropertyValue("dbEnv")
    def orgAlias = testRunner.testCase.testSuite.project.getPropertyValue ("OrgAlias")
log.info "dbEnv = " + dbEnv
// Get Client id, client name
    Configuration configuration = ConfigurationLoader.loadConfiguration(orgAlias);
    daoManagerClientWebservice = new DaoManagerClientWS(configuration.getRpmDatabase())

def clientId =  context.expand('${#TestCase#ClientId}').toUpperCase()

log.info clientId
// Get ClientId in Db and compare clientId value testcase-level
daoManagerXifinRpm = new DaoManagerXifinRpm(configuration.getRpmDatabase())
def clientIdInDb = daoManagerXifinRpm.checkClientInfoInDb(clientId, dbEnv)
assert clientIdInDb == true: "AddClient-AddNewClientWithRequiredFieldsOnly failed"

def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def holder = groovyUtils.getXmlHolder( "Step2-AddNewClientWithRequiredFieldsOnly#Response" )

//If the desired content is namespace qualified (very likely for SOAP responses), you need to define the namespace first.
holder.namespaces["ns3"] = "http://www.xifin.com/schema/client"
def message = holder["//ns3:StatusMessage[1]/text()"]
// Set Status message testcase -level Value
testRunner.testCase.setPropertyValue("StatusMessage", message)
log.info "StausMessage = " + message
assert message == "Success" : "AddClient-AddNewClientWithRequiredFieldsOnly failed"
